import React, {Component} from 'react';
import TodoItem from './TodoItem';

 class TodoList extends Component {

    deleteHandler(id) {
        console.log("id is:" +id);
         let items = this.props.items;
         let index = items.findIndex(Delete => Delete.id === id);
         items.splice(index,1);
         this.setState({items:items});

    }   

    render() {
        let items;

        if(this.props.items){
            items = this.props.items.map(item => {
                return (<ol key={item}><TodoItem item={item} deleted={() => this.deleteHandler.bind(this.props.id)}/></ol>);
            });
        }

        return (
            <ul className="list-group my-5">
                <h3 className="text-center">ToDo List</h3>
                {items}
                <button type="button" className="btn btn-danger btn-block mt-5">Clear List</button> 

            </ul>

        );
    }

}

export default TodoList;
