import React, { useState, useEffect } from 'react';

 const todoInput = () => {

    const [newItem, setNewItem] = useState(0);

    useEffect( () => {
        function handleSubmit(e) {

            if(this.refs.item.value === '' ){
                alert("Please insert input");
            }
            else{
                setNewItem(this.refs.item.value);
            }

            e.preventDefault();
        };
    });

    return (
        <div className="card card-body my-3">
            <form onSubmit={handleSubmit}>
                <div className="input-group">
                    {/* <div className="input-group-prepend">
                        <div className="input-group-text bg-primary text-white">
                            <i className="fas fa-book"></i>
                        </div>
                    </div> */}
                    <input type="text" ref="item" className="form-control text-capitalize text-center" placeholder="insert todo item"/>
                </div>
                <button type="submit" className="btn btn-block btn-primary mt-3">Add item</button>
            </form>

        </div>
    );

}

export default todoInput;
